// SPDX-License-Identifier: MIT
pragma solidity ^0.8.3;

/// @title AssistantMath Library
/// @dev Mathematical helper functions for assistant contracts
library AssistantMath {

    /// @dev Returns the maximum of two integers
    function max(int a, int b) internal pure returns (int) {
        return a > b ? a : b;
    }

    /// @dev Calculates the square root using the Babylonian method
    /// @param y The number to calculate the square root of
    /// @return z The square root
    function sqrt(uint y) internal pure returns (uint z) {
        if (y > 3) {
            z = y;
            uint x = y / 2 + 1;
            while (x < z) {
                z = x;
                x = (y / x + x) / 2;
            }
        } else if (y != 0) {
            z = 1;
        }
    }

    /// @dev Calculates gas cost in stake tokens
    /// @param gas The gas used
    /// @param num Numerator of price ratio
    /// @param den Denominator of price ratio
    /// @return The gas cost in stake tokens
    function getGasCostInStakeTokens(uint gas, uint num, uint den) internal view returns (uint) {
        return gas * tx.gasprice * num / den;
    }
}

