// SPDX-License-Identifier: MIT
pragma solidity ^0.8.3;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./CounterstakeFactory.sol";
import "./AssistantFactory.sol";

/**
 * @title BridgeRegistry
 * @dev Registry contract to track all bridges and assistants created by factories
 * Provides easy querying and management of bridge instances
 */
contract BridgeRegistry is Ownable {

    // Bridge information structure - Minimal data to avoid duplication
    struct BridgeInfo {
        address bridgeAddress;
        BridgeType bridgeType;
        bool isActive;
        uint256 createdAt;
        string metadata; // JSON string for additional data
    }

    // Assistant information structure - Minimal data to avoid duplication
    struct AssistantInfo {
        address assistantAddress;
        address bridgeAddress;
        AssistantType assistantType;
        bool isActive;
        uint256 createdAt;
        string metadata; // JSON string for additional data
    }

    enum BridgeType { Export, ImportWrapper }
    enum AssistantType { ExportAssistant, ImportWrapperAssistant }

    // Mappings to track bridges and assistants
    mapping(address => BridgeInfo) public bridges;
    mapping(address => AssistantInfo) public assistants;

    // Arrays to maintain order and enable enumeration
    address[] public allBridges;
    address[] public allAssistants;

    // Mappings for quick lookups
    mapping(address => address[]) public assistantsByBridge;

    // Factory addresses
    address public counterstakeFactory;
    address public assistantFactory;

    // Events
    event BridgeRegistered(
        address indexed bridgeAddress,
        string network,
        string asset,
        BridgeType bridgeType,
        uint256 timestamp
    );

    event AssistantRegistered(
        address indexed assistantAddress,
        address indexed bridgeAddress,
        string name,
        string symbol,
        AssistantType assistantType,
        uint256 timestamp
    );

    event BridgeDeactivated(address indexed bridgeAddress);
    event AssistantDeactivated(address indexed assistantAddress);
    event BridgeReactivated(address indexed bridgeAddress);
    event AssistantReactivated(address indexed assistantAddress);

    event FactoryUpdated(address indexed factoryAddress, string factoryType);

    modifier onlyFactory() {
        require(
            msg.sender == counterstakeFactory || msg.sender == assistantFactory,
            "BridgeRegistry: Only factory can call this function"
        );
        _;
    }

    constructor(address _counterstakeFactory, address _assistantFactory) {
        counterstakeFactory = _counterstakeFactory;
        assistantFactory = _assistantFactory;
    }

    /**
     * @dev Register a new Export bridge
     * @param bridgeAddress The address of the created bridge
     */
    function registerExportBridge(
        address bridgeAddress
    ) external onlyFactory {
        require(bridgeAddress != address(0), "BridgeRegistry: Invalid bridge address");
        require(bridges[bridgeAddress].bridgeAddress == address(0), "BridgeRegistry: Bridge already registered");

        BridgeInfo memory newBridge = BridgeInfo({
            bridgeAddress: bridgeAddress,
            bridgeType: BridgeType.Export,
            isActive: true,
            createdAt: block.timestamp,
            metadata: ""
        });

        bridges[bridgeAddress] = newBridge;
        allBridges.push(bridgeAddress);

        emit BridgeRegistered(bridgeAddress, "", "", BridgeType.Export, block.timestamp);
    }

    /**
     * @dev Register a new ImportWrapper bridge
     * @param bridgeAddress The address of the created bridge
     */
    function registerImportWrapperBridge(
        address bridgeAddress
    ) external onlyFactory {
        require(bridgeAddress != address(0), "BridgeRegistry: Invalid bridge address");
        require(bridges[bridgeAddress].bridgeAddress == address(0), "BridgeRegistry: Bridge already registered");

        BridgeInfo memory newBridge = BridgeInfo({
            bridgeAddress: bridgeAddress,
            bridgeType: BridgeType.ImportWrapper,
            isActive: true,
            createdAt: block.timestamp,
            metadata: ""
        });

        bridges[bridgeAddress] = newBridge;
        allBridges.push(bridgeAddress);

        emit BridgeRegistered(bridgeAddress, "", "", BridgeType.ImportWrapper, block.timestamp);
    }

    /**
     * @dev Register a new ExportAssistant
     * @param assistantAddress The address of the created assistant
     * @param bridgeAddress The bridge address this assistant manages
     */
    function registerExportAssistant(
        address assistantAddress,
        address bridgeAddress
    ) external onlyFactory {
        require(assistantAddress != address(0), "BridgeRegistry: Invalid assistant address");
        require(assistants[assistantAddress].assistantAddress == address(0), "BridgeRegistry: Assistant already registered");

        AssistantInfo memory newAssistant = AssistantInfo({
            assistantAddress: assistantAddress,
            bridgeAddress: bridgeAddress,
            assistantType: AssistantType.ExportAssistant,
            isActive: true,
            createdAt: block.timestamp,
            metadata: ""
        });

        assistants[assistantAddress] = newAssistant;
        allAssistants.push(assistantAddress);
        assistantsByBridge[bridgeAddress].push(assistantAddress);

        emit AssistantRegistered(assistantAddress, bridgeAddress, "", "", AssistantType.ExportAssistant, block.timestamp);
    }

    /**
     * @dev Register a new ImportWrapperAssistant
     * @param assistantAddress The address of the created assistant
     * @param bridgeAddress The bridge address this assistant manages
     */
    function registerImportWrapperAssistant(
        address assistantAddress,
        address bridgeAddress
    ) external onlyFactory {
        require(assistantAddress != address(0), "BridgeRegistry: Invalid assistant address");
        require(assistants[assistantAddress].assistantAddress == address(0), "BridgeRegistry: Assistant already registered");

        AssistantInfo memory newAssistant = AssistantInfo({
            assistantAddress: assistantAddress,
            bridgeAddress: bridgeAddress,
            assistantType: AssistantType.ImportWrapperAssistant,
            isActive: true,
            createdAt: block.timestamp,
            metadata: ""
        });

        assistants[assistantAddress] = newAssistant;
        allAssistants.push(assistantAddress);
        assistantsByBridge[bridgeAddress].push(assistantAddress);

        emit AssistantRegistered(assistantAddress, bridgeAddress, "", "", AssistantType.ImportWrapperAssistant, block.timestamp);
    }

    // Query functions

    /**
     * @dev Get all bridges
     * @return Array of bridge addresses
     */
    function getAllBridges() external view returns (address[] memory) {
        return allBridges;
    }

    /**
     * @dev Get all assistants
     * @return Array of assistant addresses
     */
    function getAllAssistants() external view returns (address[] memory) {
        return allAssistants;
    }



    /**
     * @dev Get assistants by bridge
     * @param bridgeAddress The bridge address
     * @return Array of assistant addresses for the bridge
     */
    function getAssistantsByBridge(address bridgeAddress) external view returns (address[] memory) {
        return assistantsByBridge[bridgeAddress];
    }



    /**
     * @dev Get active bridges
     * @return Array of active bridge addresses
     */
    function getActiveBridges() external view returns (address[] memory) {
        address[] memory activeBridges = new address[](allBridges.length);
        uint256 activeCount = 0;

        for (uint256 i = 0; i < allBridges.length; i++) {
            if (bridges[allBridges[i]].isActive) {
                activeBridges[activeCount] = allBridges[i];
                activeCount++;
            }
        }

        // Resize array to actual count
        assembly {
            mstore(activeBridges, activeCount)
        }

        return activeBridges;
    }

    /**
     * @dev Get active assistants
     * @return Array of active assistant addresses
     */
    function getActiveAssistants() external view returns (address[] memory) {
        address[] memory activeAssistants = new address[](allAssistants.length);
        uint256 activeCount = 0;

        for (uint256 i = 0; i < allAssistants.length; i++) {
            if (assistants[allAssistants[i]].isActive) {
                activeAssistants[activeCount] = allAssistants[i];
                activeCount++;
            }
        }

        // Resize array to actual count
        assembly {
            mstore(activeAssistants, activeCount)
        }

        return activeAssistants;
    }

    /**
     * @dev Get bridge count
     * @return Total number of bridges
     */
    function getBridgeCount() external view returns (uint256) {
        return allBridges.length;
    }

    /**
     * @dev Get assistant count
     * @return Total number of assistants
     */
    function getAssistantCount() external view returns (uint256) {
        return allAssistants.length;
    }

    /**
     * @dev Get bridge info by address
     * @param bridgeAddress The bridge address
     * @return BridgeInfo struct
     */
    function getBridgeInfo(address bridgeAddress) external view returns (BridgeInfo memory) {
        require(bridges[bridgeAddress].bridgeAddress != address(0), "BridgeRegistry: Bridge not found");
        return bridges[bridgeAddress];
    }

    /**
     * @dev Get assistant info by address
     * @param assistantAddress The assistant address
     * @return AssistantInfo struct
     */
    function getAssistantInfo(address assistantAddress) external view returns (AssistantInfo memory) {
        require(assistants[assistantAddress].assistantAddress != address(0), "BridgeRegistry: Assistant not found");
        return assistants[assistantAddress];
    }

    // Admin functions

    /**
     * @dev Deactivate a bridge (only owner)
     * @param bridgeAddress The bridge address to deactivate
     */
    function deactivateBridge(address bridgeAddress) external onlyOwner {
        require(bridges[bridgeAddress].bridgeAddress != address(0), "BridgeRegistry: Bridge not found");
        bridges[bridgeAddress].isActive = false;
        emit BridgeDeactivated(bridgeAddress);
    }

    /**
     * @dev Reactivate a bridge (only owner)
     * @param bridgeAddress The bridge address to reactivate
     */
    function reactivateBridge(address bridgeAddress) external onlyOwner {
        require(bridges[bridgeAddress].bridgeAddress != address(0), "BridgeRegistry: Bridge not found");
        bridges[bridgeAddress].isActive = true;
        emit BridgeReactivated(bridgeAddress);
    }

    /**
     * @dev Deactivate an assistant (only owner)
     * @param assistantAddress The assistant address to deactivate
     */
    function deactivateAssistant(address assistantAddress) external onlyOwner {
        require(assistants[assistantAddress].assistantAddress != address(0), "BridgeRegistry: Assistant not found");
        assistants[assistantAddress].isActive = false;
        emit AssistantDeactivated(assistantAddress);
    }

    /**
     * @dev Reactivate an assistant (only owner)
     * @param assistantAddress The assistant address to reactivate
     */
    function reactivateAssistant(address assistantAddress) external onlyOwner {
        require(assistants[assistantAddress].assistantAddress != address(0), "BridgeRegistry: Assistant not found");
        assistants[assistantAddress].isActive = true;
        emit AssistantReactivated(assistantAddress);
    }

    /**
     * @dev Update factory addresses (only owner)
     * @param _counterstakeFactory New counterstake factory address
     * @param _assistantFactory New assistant factory address
     */
    function updateFactories(address _counterstakeFactory, address _assistantFactory) external onlyOwner {
        counterstakeFactory = _counterstakeFactory;
        assistantFactory = _assistantFactory;

        emit FactoryUpdated(_counterstakeFactory, "CounterstakeFactory");
        emit FactoryUpdated(_assistantFactory, "AssistantFactory");
    }

    /**
     * @dev Update bridge metadata (only owner)
     * @param bridgeAddress The bridge address
     * @param metadata The new metadata JSON string
     */
    function updateBridgeMetadata(address bridgeAddress, string memory metadata) external onlyOwner {
        require(bridges[bridgeAddress].bridgeAddress != address(0), "BridgeRegistry: Bridge not found");
        bridges[bridgeAddress].metadata = metadata;
    }

    /**
     * @dev Update assistant metadata (only owner)
     * @param assistantAddress The assistant address
     * @param metadata The new metadata JSON string
     */
    function updateAssistantMetadata(address assistantAddress, string memory metadata) external onlyOwner {
        require(assistants[assistantAddress].assistantAddress != address(0), "BridgeRegistry: Assistant not found");
        assistants[assistantAddress].metadata = metadata;
    }
}

